$("#status").change(function(){var a=$("#status").val();if(a.substr(0,6)=="/giphy"){var b=a.substr(7);var c="http://api.giphy.com/v1/gifs/search?q="+b+"&api_key=dc6zaTOxFJmzC";$.getJSON(c,function(d){var e=d.data[0].images.fixed_height_small.url;$("<div/>").addClass("message").append($("<img/>").attr("src",e)).appendTo(".messages");});}else{$("<div/>").addClass("message").append($("<img/>").attr("src","img/avatar.png")).append($("<p/>").html(a)).appendTo(".messages");}});